var mongodb = require('mongodb');
var server = new mongodb.Server("127.0.0.1", 27017, {safe: false});
var jiejie = new mongodb.Db('jiejie', server, {});

exports.mongodb = mongodb;

exports.dbServer = jiejie;
exports.client = undefined;

jiejie.open(function (error, client) {
    exports.client = !error ? client : undefined;
    console.log(exports.open);
});

jiejie.on('close', function () {
    console.log('Database connection is disconnected!\t' + new Date().toLocaleTimeString());
});
